      listenshowpage1() {
        return this.$store.state.mainPoint;
      }
    },
    watch: {
      listenshowpage1: function (old, newd) {
        // console.log(old)
        this.mainPointArr = old;
        this.flag=!this.flag;
        console.log(this.flag)
        this.changeCity();
      }
    },
    methods: {
      initMap() {
        window.onload = function () {}
        mapWorld.init().then((T) => {
          this.T = T;
          //初始化地图对象
          this.zoom = '10';
          this.map = new T.Map("mapDiv");
          //设置显示地图的中心点和级别
          this.map.centerAndZoom(new T.LngLat(this.mainPointArr[0], this.mainPointArr[1]), this.zoom);

          //创建对象
          var ctrl = new T.Control.MapType();
          //添加控件
          var scale = new T.Control.Scale();
          //添加比例尺控件
          this.map.addControl(scale);
          // this.map.addControl(ctrl);
          this.map.centerAndZoom(new T.LngLat(this.mainPointArr[0], this.mainPointArr[1]), this.zoom);
          this.control = new T.Control.Zoom();
          this.map.addControl(this.control);
          this.drawHeatmap();
          this.conHeatmap();
          this.conArea();
        }).catch(error => {
          console.log(error)
        })
      },
      changeCity(){
        this.map.centerAndZoom(new T.LngLat(this.mainPointArr[0], this.mainPointArr[1]), this.zoom);
        this.conArea();
      },
      conHeatmap() {
        if (this.checkHeatMap == true) {
          this.heatmap.show();
        } else {
          this.heatmap.hide();
        }

      },
      drawHeatmap() {
        let heatmapOverlay = new this.T.HeatmapOverlay({
          "radius": 50,
          //visible: false
        });
        this.map.addOverLay(heatmapOverlay);
        heatmapOverlay.setDataSet({
          data: this.points,
          max: 300
        });
        this.heatmap = heatmapOverlay;
      },
      conArea() {
        if (this.checkArea == true && this.flag==false) {
          this.drawArea(this.pds_areaArr[0],0);
        }else if(this.checkArea == true && this.flag==true){
          for (let i = 0, aLen = this.sz_areaArr.length; i < aLen; i++) {
            this.drawArea(this.sz_areaArr[i],i);
          };
        }else if(this.checkArea == false && this.flag==true) {
          for (let i = 0, aLen = this.sz_areaArr.length; i < aLen; i++) {
            this.szPoly[i].hide();
          };
        }else if(this.checkArea == false && this.flag==false){
          for (let i = 0, aLen = this.pds_areaArr.length; i < aLen; i++) {
            this.szPoly[i].hide();
          };
        }
      },
      drawArea(json,num) {
        let area = json.area;
        let points = area.points;
        if (points && points.length != 0) {
          let style = {
            strokeColor: "#0027eb",
            Weight: 1,
            fillOpacity: 0,
            lineStyle: "dashed" //虚线
          }
          for (let i = 0; i < points.length; i++) {
            let region = points[i].region; //单个面
            let pointArr = region.split(",");
            let pointsz = [];
            for (let j = 0; j < pointArr.length - 1; j++) {
              let p = pointArr[j];
              let pArr = p.split(" ");
              let point = new this.T.LngLat(pArr[0], pArr[1]);
              pointsz.push(point);
            }
            this.szPoly[num] = new this.T.Polygon(pointsz, style);
            this.map.addOverLay(this.szPoly[num]);
          }
        }
      },

      handleChange() {}

    }
